import { Data, animate, Override, Animatable } from 'framer'

const data = Data({ dragging: false })

export const Draggable1: Override = () => {
	return {
		onValueChange(props) {
			console.log('items changed', props)
		},
		onDragStart(props) {
			data.dragging = true
		},
		onDragEnd(props) {
			data.dragging = false
			console.log('hello world', props)
		},
	}
}

export const ShowDragging: Override = () => {
	return {
		opacity: data.dragging ? 1 : 0,
	}
}
